import React from "react";
import { Button, Card, DeleteOutlineIcon, Typography, makeStyles } from "@material-ui/core"

function PostCard() {
    const useStyles = makeStyles({
        btn: {
            margin: 10
        }, 
        post: {
            margin: 40,
            padding: 20
        },
        postHeader: {
            display: "flex",
            alignItems: "center"
        }
    });

    return (
        <Card className={classes.post} key={post.id}> 
            <div className={classes.postHeader}>
                <Typography
                    variant="h4"
                    component="h2"
                >   
                    {post.title}
                </Typography>
                {isAuth && auth.currentUser.uid === post.author.id && (
                    <Button 
                        className = {classes.btn}
                        onClick={() => {
                            deletePost(post.id)
                        }}
                        startIcon={<DeleteOutlineIcon/>}
                    >
                        Delete
                    </Button>
                )}   
            </div>
            <Typography>
                {post.postText}
            </Typography>
            <Typography
                variant="h6"
            >
                @{post.author.name}
            </Typography>
        </Card>
    );
}

export default PostCard
